import React, { useState } from "react";
import { Row, Col,Button } from "react-bootstrap";
import Navigation from "../Component/Navigation";
import "./Style.css"
import Main from "./Main";
import {BsTextareaResize} from 'react-icons/bs'

const Login = () =>{

    const [count , setCount] = useState(false);
    const [count2 , setCount2] = useState(false);
    return(
        <>
        <div>
            
              {/* Ternary Operator */}
              {
                  !count ? 
                  <Row>
                  <Col></Col>
                  {/* Login Page */}
                  <Col > 
                  <Row className="red center mt-4">< BsTextareaResize className="color1"/><h1>Resume Builder</h1></Row>
                   <h1 className="mt-5 mb-3 ml-5 ">Login Page</h1>
                   <input type="email" placeholder="Username" className="email mt-2 mb-3 ml-4 mr-4" />
                   <input type="password" placeholder="Password" className="email mb-3 ml-4 mr-4" />
                   <Row>
                   <Col><button onClick={() => {setCount(true)}} className="btn btn-dark center">Subbmit</button></Col>
                   
                   {/* Forgoat Part */}
                   {
                       !count2 ? <Col><Button onClick={() => {setCount2(true)}} variant="outline-secondary">Forgoat</Button></Col>
                    :
                    <Row className="mt-5 cantainer">
                    <h1>Forgoat Password</h1>
                    <input type="email" placeholder="Enter Your Register Email" className="email ml-5" />
                    <Col><button onClick={() => {setCount2(false)}} className="btn btn-dark center mt-3">Subbmit</button></Col>
                    </Row>
                    }
                   </Row>
                  </Col>
                  <Col></Col>
              </Row>
              :
              <>
              <Row><Navigation/></Row>
              <Main/>
              </>
              }

           

       
        </div>
        </>
    )
};

export default Login
