import React, { useState, useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap";
// import { BsLink } from "react-icons/bs";
import Navigation from "../Component/Navigation";
import Footer from "./Footer";
import { GiIndianPalace } from "react-icons/gi";
import { BsPhoneFill, BsNewspaper } from "react-icons/bs";
import { BiCameraMovie } from "react-icons/bi";
import {
  MdNightShelter,
  MdScience,
  MdEmojiTransportation,
} from "react-icons/md";
import {SiPython} from "react-icons/si"
import { FcSportsMode } from "react-icons/fc";
import {GrReactjs} from "react-icons/gr"
import {BsStack} from "react-icons/bs"
import {SiMongodb} from "react-icons/si"
import {GiCyberEye} from "react-icons/gi"
import {GiBattleMech} from "react-icons/gi"
import {GiArtificialIntelligence} from "react-icons/gi"
import {SiNodedotjs} from "react-icons/si"
import {FaJava} from "react-icons/fa"
import IndiaApi from "./IndiaApi";
import TechApi from "./TechApi";
import BusinessApi from "./BusinessApi";
import EntertainmentApi from "./EntertainmentApi";
import GeneralApi from "./GeneralApi";
import HelthApi from "./HelthApi";
import ScienceApi from "./ScienceApi";
import SportsApi from "./SportsApi";
import MernApi from "./API/MernApi";
import CyberSecqurityApi from "./API/CyberSecqurityApi";
import MlApi from "./API/MlApi";
import AiApi from "./API/AiApi";
import FullStackApi from "./API/FullStackApi";
import NodeApi from "./API/NodeJsApi";
import JavaApi from "./API/JavaApi";
import ReactApi from "./API/ReactApi";
import PythonApi from "./API/PythonApi";

const Blog = () => {
  const [ind, setInd] = useState(true);
  const [tech, setTech] = useState(true);
  const [business, setBusiness] = useState(true);
  const [entertainment, setEntertainment] = useState(true);
  const [general, setGeneral] = useState(true);
  const [helth, setHelth] = useState(true);
  const [science, setScience] = useState(true);
  const [soprts, setSports] = useState(true);
  const [react, setReact] = useState(true);
  const [node, setNode] = useState(true);
  const [python, setPython] = useState(true);
  const [java, setJava] = useState(true);
  const [ai, setAi] = useState(true);
  const [ml, setMl] = useState(true);
  const [fullStack, setFullStack] = useState(true);
  const [mern, setMern] = useState(true);
  const [cyberSecqurity, setCyberSecqurity] = useState(true);
  return (
    <>
      <Row>
        <Navigation />
      </Row>
      <div className=" mt-3 mb-3 container-icon">
        {/* India Api */}
        {ind ? (
         
            
          <button className="button-icon">
            <GiIndianPalace
              onClick={() => {
                setInd(false);
              }}
              className="ind-icon"
            />
          <p>India</p>
          </button>
       
        ) : (
          <IndiaApi data={setInd} />
        )}
        {/* Tech Api */}
        {tech ? (
          <button className="button-icon">
            <BsPhoneFill
              onClick={() => {
                setTech(false);
              }}
              className="tech-icon"
            />
            <p>Technology</p>
          </button>
        ) : (
          <TechApi data={setTech} />
        )}
        {/* Business Api */}
        {business ? (
          <button className="button-icon">
            <MdEmojiTransportation
              onClick={() => {
                setBusiness(false);
              }}
              className="business-icon"
            />
            <p>Business</p>
          </button>
        ) : (
          <BusinessApi data={setBusiness} />
        )}
        {/* Enteraintment Api */}
        {entertainment ? (
          <button className="button-icon">
            <BiCameraMovie
              onClick={() => {
                setEntertainment(false);
              }}
              className="entertainment-icon"
            />
            <p>Bollywood</p>
          </button>
        ) : (
          <EntertainmentApi data={setEntertainment} />
        )}
        {/* General Api */}

        {general ? (
          <button className="button-icon">
            <BsNewspaper
              onClick={() => {
                setGeneral(false);
              }}
              className="general-icon"
            />
            <p>General</p>
          </button>
        ) : (
          <GeneralApi data={setGeneral} />
        )}
        {/* Helth Api */}
        {helth ? (
          <button className="button-icon">
            <MdNightShelter
              onClick={() => {
                setHelth(false);
              }}
              className="helth-icon"
            />
            <p>Helth</p>
          </button>
        ) : (
          <HelthApi data={setHelth} />
        )}
