import React, { useState, useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap";
import {BsLink} from "react-icons/bs"
import Navigation from "../Component/Navigation"
import Footer from "./Footer";
import {GiIndianPalace} from "react-icons/gi"
import {BsPhoneFill , BsNewspaper} from "react-icons/bs"
import {BiCameraMovie} from "react-icons/bi"
import {MdNightShelter , MdScience , MdEmojiTransportation} from "react-icons/md"
import {FcSportsMode} from "react-icons/fc"
import IndiaApi from "./IndiaApi";
import TechApi from "./TechApi";
import BusinessApi from "./BusinessApi";
import EntertainmentApi from "./EntertainmentApi";
import GeneralApi from "./GeneralApi";
import HelthApi from "./HelthApi";
import ScienceApi from "./ScienceApi";
import SportsApi from "./SportsApi";

const Blog = () => {
 
  const [ind , setInd] = useState(true)
  const [tech , setTech] = useState(true)
  const [business , setBusiness] = useState(true)
  const [entertainment , setEntertainment] = useState(true)
  const [general , setGeneral] = useState(true)
  const [helth , setHelth] = useState(true)
  const [science , setScience] = useState(true)
  const [soprts , setSports] = useState(true) 
  return (
    <>
     <Row><Navigation /></Row>
     <div className=" mt-3 mb-3 container-icon" >
       {/* India Api */}
       {
         ind ? <button className="button-icon"><GiIndianPalace onClick={() => {setInd(false)}} className="ind-icon" /></button>
         : <IndiaApi data={setInd}/>
       }
       {/* Tech Api */}
    {
      tech ? <button className="button-icon"><BsPhoneFill onClick={() =>{setTech(false)}} className="tech-icon" /></button>
      : <TechApi data={setTech} />
    }
    {/* Business Api */}
    {
      business ? <button className="button-icon"><MdEmojiTransportation onClick={() =>{setBusiness(false)}} className="business-icon" /></button>
      : <BusinessApi data={setBusiness} />
    }
    {/* Enteraintment Api */}
    {entertainment ? <button className="button-icon"><BiCameraMovie onClick={() =>{setEntertainment(false)}} className="entertainment-icon" /></button>
    : <EntertainmentApi data={setEntertainment} />
    }
    {/* General Api */}
    
    {general ? <button className="button-icon"><BsNewspaper onClick={() =>{setGeneral(false)}} className="general-icon" /></button>
    : <GeneralApi data={setGeneral} />
    }
    {/* Helth Api */}
    {helth ? <button className="button-icon"><MdNightShelter onClick={() =>{setHelth(false)}} className="helth-icon" /></button>
    : <HelthApi data={setHelth} />
    }
    {/* Science */}
    {science ? <button className="button-icon"><MdScience onClick={() =>{setScience(false)}} className="science-icon" /></button>
    : <ScienceApi data={setScience} />
    }
    {/* Sports */}
    { soprts ? <button className="button-icon"><FcSportsMode onClick={() =>{setSports(false)}} className="sports-icon" /></button>
    : <SportsApi data={setSports} />
    }

     </div>
     <Row><Footer /></Row>
    </>
  );
};
export default Blog;
