import React, { useState, useEffect } from "react";
import { Row, Col, Container } from "react-bootstrap";
import {BsLink} from "react-icons/bs"
import {FaHeart} from "react-icons/fa"
import Navigation from "../Component/Navigation"
import Footer from "./Footer";

const GetApi = () => {
  const [imageData, setImageData] = useState([]);
  const [serch , setSearch] = useState("")

 

 function apiFun()
{ useEffect(() =>{

    fetch(`https://pixabay.com/api/?key=27186541-7a0f9f128637850ee1860d5f0&q=${serch}&image_type=photo&pretty=true`)
    .then((promic) =>{return promic.json()})
    .then((res) => {setImageData(res.hits)})
   
 },[])}
 
function serchFun(e)
{
  e.preventDefault()
  apiFun()
}
  return (
    <>
      <Row>
       
       <Navigation />
      </Row>
      {/* Search Icon */}
      <Row className="mt-5 mb-5 center">
       <Col></Col>
       <Col>
       <input type="text" value={serch} onChange={(e) => {setSearch(e.target.value)}} />
        <button onClick={serchFun}>Search</button>
       </Col>
       <Col></Col>
      </Row>
      <Row>
        <span>
          {
            imageData.map((key , id) =>{
              return(
              <>
                <img src={key.webformatURL} style={{width: 400 ,marginLeft: 10 , marginTop: 10}}/>
              </>
            )})
          }
        </span>
      </Row>
      <Row className="mt-5"><Footer /></Row>
    </>
  );
};
export default GetApi;
