import React, { useState, useEffect } from "react";
import { Row, Col } from "react-bootstrap";
import Navigation from "./Navigation";

const Api = () => {
  const [getapipromic, setGetapipromic] = useState("");
 const [apiarray, setApiarray] = useState([]);



  useEffect(() => {
    fetch("https://reqres.in/api/users/2", { method: "GET" })
      .then((apipromic) => {
        //console.log(apipromic)
        return apipromic.json();
      })
      .then((apidata) => {
        //console.log(apidata.data)
        setGetapipromic(apidata.data);
      });
  }, []);
  console.log(getapipromic);

  return (
    <>
      <Navigation />
      <Row>
        <Col></Col>
        <Col>
<Row>Fetch Data from Singal User.</Row>
          <Row className="mt-5">First Name: {"  "}{getapipromic.first_name}</Row>
          
          <Row>Last Name: {"  "} {getapipromic.last_name}</Row>
          <Row>ID: {"  "} {getapipromic.id}</Row>
          <Row>Email: {"  "} {getapipromic.email}</Row>
          <Row>

              <img src={getapipromic.avatar} alt="" height="180px" width="50px" />
          </Row>
        </Col>
        <Col></Col>
      </Row>
    </>
  );
};
export default Api;
