import React, { useState, useEffect } from "react";
import { Row, Col } from "react-bootstrap";
import Navigation from "../Component/Navigation";

const Api = () => {
  // Input Variable
  const [name , setName] = useState("")
  const [email , setEmail] = useState("")
  const [news , setNews] = useState([])
  // Calling api with get method
  useEffect(()=>{
   getApi()
  }, [])
  // Get Api Function
  function getApi()
  {
    fetch("http://localhost:3000/articles").then((response)=>{
      response.json().then((promic2) =>{
          
        setNews(promic2)
        setName(promic2[0].name)
        setEmail(promic2[0].email)
       //  console.log(news)
      })
    })
  }
  // Update Api Functoin
  function uploadFun()
  {
    console.log({name,email})
    const data = {name,email}
    fetch("http://localhost:3000/articles" , {
      method:'POST',
      headers:{
        'Accept':'application/json',
        'Content-Type':'application/json'
      },
      body:JSON.stringify(data)
    }).then((result)=>{
      result.json((result2)=>{
        
      })
    })
    getApi()
    setName("")
    setEmail("")
  }

  // Delete Api Function

  function deleteFun(id)
  {
    fetch(`http://localhost:3000/articles/${id}`,
    {method:"DELETE"}).then((promic) =>{
      promic.json().then((promic2) =>{
        getApi()
      })
    })
  }


  // Edit Api Function
  function editApi(id)
  {
    console.log("news",news[id-1])
  }

  return (
    <>
      <Row><Navigation /></Row>
      
      <Row className="mt-5 mb-5 ">
        
        <Col className="">
        {/* Post Method Api */}
        <input type="text" value={name} onChange={(e) => {setName(e.target.value)}} /> <br /> <br />
        <input type="email" value={email} onChange={(e) =>{setEmail(e.target.value)}} /> <br /> <br />
        <button onClick={uploadFun}> Update</button>
        </Col>
        <Col>
          {
              news.map((key) =>{
                  return(
                      <>
                      <Row className="center">
                        <Col>{key.id}</Col>
                        <Col>{key.name}</Col>
                        <Col>{key.email}</Col>
                        <Col>
                        <button onClick={() =>{deleteFun(key.id)}}>Delete</button>
                        </Col>
                        <Col>
                        <button onClick={()=>{editApi(key.id)}}>
                        Edit</button>
                        </Col>
                      </Row>
                      
                         
                      
                      </>
                  )
              })
          }
         
        </Col>
        
      </Row>
    </>
  );
};
export default Api;
