import React,{useState,useEffect} from "react";
import { Row } from "react-bootstrap";
import Navigation from "./Navigation"

const Api = () =>{

    const [getapipromic,setGetapipromic] = useState("")

    useEffect(() => {
        fetch("https://reqres.in/api/users/2",
        { method: 'GET'}
        )
        .then(
            (apipromic) => {
               //console.log(apipromic)
                return apipromic.json()
            }
        )
        .then(
            (apidata) => {
                //console.log(apidata.data)
                setGetapipromic(apidata.data)
            }
        )
    } ,[])
    console.log(getapipromic)

    return(
        <>
        <Navigation/>

        <Col></Col>
        <Col>
        <Row>

            Name: {" "} {getapipromic.first_name}
        </Row>
        
        </Col>
        <Col></Col>
        </>
    )
}
export default Api
